// write-Data-To-Juno

import { useEffect, useState } from "react";
import { initJuno, setDoc } from "@junobuild/core";

function App() {
  const [record, setRecord] = useState(undefined);
  const [data, setData] = useState({});


  const list = async () => {
    const { data } = await listDocs({
      collection: "esp32data",
      filter: {},
    });

    setItems(data);
  };

  console.log(data);
  // TODO: Replace 'satelliteId' with your actual satellite ID
  useEffect(() => {
    (async () =>
      await initJuno({
        satelliteId: "nizxm-piaaa-aaaal-admxa-cai",
      }))();
  }, []);

  const insert = async (esp32Data) => {
    const doc = await setDoc({
      collection: "esp32-data",
      doc: {
        key: `my-key-${new Date().getTime()}`,
        data: {
          esp32Data,
        },
      },
    });

    setRecord(doc);
  };

  const handleEsp32Data = async () => {
    // Assuming esp32Data is the payload received from ESP32
    const esp32Data = {
      "_id": {"$oid": "65a2525597b55edd73691628"},
      "ip": "154.154.88.255",
      "hostname": "255-88-154-154.r.airtelkenya.com",
      "city": "Nairobi",
      "region": "Nairobi Area",
      "country": "KE",
      "loc": "-1.2833,36.8167",
      "org": "AS36926 Airtel Networks Kenya Limited",
      "timezone": "Africa/Nairobi",
      "readme": "https://ipinfo.io/missingauth",
      "temperature": {"$numberDouble": "48.6"},
      "heartrate": {"$numberInt": "100"},
    };

    await insert(esp32Data);
  };

  return (
    <>
      <button onClick={handleEsp32Data}>Insert ESP32 data</button>
      {record !== undefined && <span>Key: {record.key}</span>}

      <div>
      {/* <>
      {data.map(({ key, data: { text } }) => (
        <p key={key}>{text}</p>
      ))}
    </> */}
      </div>
    </>
  );
}

export default App;



